$arr = str_split($input);

$floor = 0;
$position = 1;
foreach ($arr as $item)
{
	switch ($item)
	{
		case '(':
			$floor++;
			break;
		case ')';
			$floor--;
			break;
	}
	if ($floor < 0) {
		echo 'Went into basement at position: '.$position;
		exit;
	}
	$position++;
}

echo 'floor is: '.$floor;